import '../assets/schedule.css';
import {jwtDecode} from 'jwt-decode';
import {useEffect, useState} from 'react';

function Schedule (){
    const dates = jwtDecode(localStorage.getItem('token'));
    const code = dates.institution;
    const [scheduleDate, setScheduleData] = useState([]);
    const [cunterCells, setCounterCells] = useState(72);
    useEffect(() => {
            const requestSchedule = async () => {
            try{
                const response = await fetch('http://localhost:5000/schedule', {
                    method: 'POST',
                    headers: {
                        'content-type' : 'application/json',
                    },
                    body: JSON.stringify({
                        type: '1',
                        grade: '6',
                        code: code,
                    })
                });
                if(response.status === 200){
                    const schedule = await response.json();
                    console.log(schedule);
                    setScheduleData(schedule);
                }else{
                    console.log('codigo invalido');
                }
            }
            catch(error){
                console.error("error en la solicitud: ", error);
            }
        };
        requestSchedule();
    }, [code]);
    return(
        <div className='Schedule-container'>
            <div className='Main-hour-container'>
                <div className='Left-hour-container'>
                    <span className='Span-hour'></span>
                    <span className='Span-hour'>7:00 AM</span>
                    <span className='Span-hour'></span>
                    <span className='Span-hour'>9:00 AM</span>
                    <span className='Span-hour'></span>
                    <span className='Span-hour'>11:00 AM</span>
                    <span className='Span-hour'></span>
                    <span className='Span-hour'>1:00 PM</span>
                    <span className='Span-hour'></span>
                    <span className='Span-hour'>3:00 PM</span>
                    <span className='Span-hour'></span>
                    <span className='Span-hour'>5:00 PM</span>
                    <span className='Span-hour'></span>
                </div>
                <div className='Top-hour-container'>
                    <span>Lunes</span>
                    <span>Martes</span>
                    <span>Miercoles</span>
                    <span>Jueves</span>
                    <span>Viernes</span>
                    <span>Sabado</span>
                </div>
                {scheduleDate.map((hour, index) => {
                    const getGridRowFromTime = (time) => {
                        const baseHour = 6; 
                        const hour = parseInt(time.split(":")[0], 10);
                        return hour - baseHour + 2;
                    };
                    const weekDays = {
                        Lunes: 2,
                        Martes: 3,
                        Miércoles: 4,
                        Jueves: 5,
                        Viernes: 6,
                        Sabado: 7,
                    };
                    setCounterCells(cunterCells - parseInt(hour.duration, 10));
                    console.log(cunterCells);
                    return(
                        <div key={index} className='Hour-container1' style={{
                                gridColumn: `${weekDays[hour.weekDay]} / span 1`,
                                gridRow: `${getGridRowFromTime(hour.startDayTime)} / span ${parseInt(hour.duration, 10)}`
                            }}>
                            <span className='text-dates'>{hour.Subject}</span>
                        </div>
                    )
                })}
                {Array.from({ length:cunterCells }, () => {

                })}
            </div>
        </div>
    );
}
export default Schedule;
