import {useState} from 'react';
import '../assets/SingIn.css';
import {Link} from 'react-router-dom';
import visivilityOffIcon from '../assets/visibility_off.svg';
import visivilityIcon from'../assets/visibility.svg';

function Login(){
    const [datesValue, setdatesValue] = useState({
        user: '',
        password: '',
    }); 
    const [visibilityValue, setvisibilityValue] = useState(false); 
    const sendDates = (e) => {
        e.preventDefault();
        fetch('http://localhost:5000/login', {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
            },
            body: JSON.stringify(datesValue)
        });
    };
    function ChangePasswordView(){
        const input = document.getElementById("inputPassword");
        
        if(input.type == "password"){
            input.type = "text";
            //(true);
        }else{
            input.type = "password";
            setvisibilityValue(false);
        }
    }
    return( 
        <div className="Main">
            <div className="textUserContent">
                <h2 className='IngresarT'>Ingresar</h2>
            </div>
            <form onSubmit={sendDates}>
                <div className="userContent">
                    <input className="inputUser" placeholder="Usuario" onChange={(e) => 
                        setdatesValue(prev => ({
                            ...prev,
                            user: e.target.value
                        }))
                    } required></input>
                </div>
                <div className="passwordContent">
                    <input type='password' className="inputPassword" id="inputPassword" placeholder="Contraseña" onChange={(e) =>
                        setdatesValue(prev => ({
                            ...prev,
                            password: e.target.value
                        }))
                    } required></input>
                    <button className='viewPassword' onClick={ChangePasswordView}><img id='visibilityIcon' src={visibilityValue ? visivilityIcon:visivilityOffIcon} alt='Ver contraseña'/></button>
                </div>
                <div className="buttonContent">
                    <button className="ButtonIn" type='submit'>Ingresar</button>
                </div>
            </form>
            <div className="createContent">
                <h3 className='questionText'>Aun no tienes cuenta?</h3>
                <Link className="CreteAccount" to="/Auth/Register">Registrar</Link>
            </div>
        </div>
    );
}
export default Login;
